$(function () {
    var time=5000,i=0,a=0;
    setTimeout(function () {
        timeSrc(i,time,a);
    },a);
})
function timeSrc(i,time,a) {
    //$("#pictureID").show();
    var imgSrc=["pictures/mob1.png","pictures/mob2.png","pictures/mob3.png","pictures/mob4.png"];
    var imgNum=imgSrc.length;
    i++;
    console.log(i);
    if (i<imgNum){
        $("#pictureID").stop().fadeOut(time);
        $("#pictureID").attr("src",imgSrc[i-1]);
        setTimeout(function () {
            timeSrc(i,time,a);
        },time);
    }
    else {
        $("#pictureID").fadeOut(time/2).fadeIn(time/2);
        $("#pictureID").attr("src",imgSrc[imgNum-1]);
        setTimeout(function () {
            timeSrc(a,time,a);
        },time);
    }
}